local p,c=p.LocalPlayer,nil p.CharacterAdded:Connect(function(ch)c=ch end)c=p.Character or p.CharacterAdded:Wait()local hrp=c:WaitForChild("HumanoidRootPart")local g=Instance.new("ScreenGui",game.CoreGui)local f=Instance.new("Frame",g)f.Size=UDim2.new(0,220,0,150)f.Position=UDim2.new(0,350,0,100)f.BackgroundColor3=Color3.fromRGB(30,30,30)f.BorderSizePixel=0
local buttons={"Fly","Invisible","NoClip"}local states={false,false,false}local bv=Instance.new("BodyVelocity",hrp)bv.MaxForce=Vector3.one*1e9 bv.P=1250 bv.Velocity=Vector3.zero bv.Enabled=false
for i,v in ipairs(buttons)do local b=Instance.new("TextButton",f)b.Size=UDim2.new(0,200,0,30)b.Position=UDim2.new(0,10,0,10+(i-1)*40)b.Text=v..": OFF"b.BackgroundColor3=Color3.fromRGB(100,0,0)b.MouseButton1Click:Connect(function()states[i]=not states[i]b.Text=v..": "..(states[i]and"ON"or"OFF")b.BackgroundColor3=states[i]and Color3.fromRGB(0,150,0)or Color3.fromRGB(100,0,0)if v=="Fly"then bv.Enabled=states[i]elseif v=="Invisible"then for _,k in pairs(c:GetDescendants())do if k:IsA("BasePart")and k.Name~="HumanoidRootPart"or k:IsA("Decal")then k.Transparency=states[i]and 1 or 0 end end end end)end
game:GetService("RunService").RenderStepped:Connect(function()if states[1]then local cf=workspace.CurrentCamera.CFrame local mv=Vector3.zero local uis=game:GetService("UserInputService")if uis:IsKeyDown(Enum.KeyCode.W)then mv+=cf.LookVector end if uis:IsKeyDown(Enum.KeyCode.S)then mv-=cf.LookVector end if uis:IsKeyDown(Enum.KeyCode.A)then mv-=cf.RightVector end if uis:IsKeyDown(Enum.KeyCode.D)then mv+=cf.RightVector end if uis:IsKeyDown(Enum.KeyCode.Space)then mv+=cf.UpVector end if uis:IsKeyDown(Enum.KeyCode.LeftControl)then mv-=cf.UpVector end bv.Velocity=mv.Magnitude>0 and mv.Unit*100 or Vector3.zero end if states[3]then for _,v in pairs(c:GetDescendants())do if v:IsA("BasePart")then v.CanCollide=false end end else for _,v in pairs(c:GetDescendants())do if v:IsA("BasePart")and v.Name~="HumanoidRootPart"then v.CanCollide=true end end end end)
